mapper.select:
SELECT pd.id id, py.orderdate 
FROM paydetail pd JOIN payrecord py ON pd.id2 = py.id
WHERE TO_CHAR( orderdate, 'yyyy-mm-dd hh24:mi:ss') = #{odate} AND py.memid = #{memid}

SELECT * FROM productimg pimg JOIN ( SELECT sop.id shippingoptionid, sop.shippingoptionname, sop.defaultshippingfee, c3.productid, c3.pdname, c3.poptionid, c3.optionprice, c3.orecordid paydetailid, c3.paydetailid payrecordid, c3.quantity, c3.memid, c3.pmethod, c3.orderamount, c3.orderdate FROM shippingoption sop JOIN ( SELECT pd.id productid, pd.shippingoptionid, pd.pdname, c2.poptionid, c2.memid, c2.paydetailid, c2.optionprice, c2.orecordid, c2.quantity, c2.pmethod, c2.orderamount, c2.orderdate FROM product pd JOIN ( SELECT po.productid, po.id poptionid, po.optionprice, c1.orecordid, c1.memid, c1.quantity, c1.paydetailid, c1.pmethod, c1.orderamount, c1.orderdate FROM productoption po JOIN ( SELECT pt.id2 orecordId , pt.quantity quantity, pt.id3 poptionid, pr.memid, pr.pmethod pmethod, pt.id paydetailid, pr.orderamount, pr.orderdate FROM paydetail pt JOIN payrecord pr ON pt.id2 = pr.id ) c1 ON po.id = c1.poptionid ) c2 ON pd.id = c2.productid ) c3 ON sop.id = c3.shippingoptionid WHERE c3.paydetailid IN
#{orderId}
) c4 ON pimg.productid = c4.productid WHERE pimg.imgcontent = 'sum'

SELECT COUNT(id) 
FROM SHIPPINGPLACEINFORMATION
WHERE memid = #{memid}

SELECT * FROM 
( 
SELECT ROWNUM no, t.* 
FROM 
( 
SELECT id, memid, addressnick, receivemem, roadaddress, jibunaddress, 
detailaddress, tel, postnum, defaultshipping 
FROM SHIPPINGPLACEINFORMATION 
WHERE memid = #{memid}
ORDER BY defaultshipping DESC, id DESC 
) t 
) b 
WHERE no BETWEEN ${pageDTO.start} and ${pageDTO.end}

SELECT id, memid, addressnick, receivemem, roadaddress, jibunaddress, detailaddress, tel, postnum 
FROM shippingPlaceInformation  
WHERE id = ${id}

mapper.update:
UPDATE shippingPlaceInformation 
SET DEFAULTSHIPPING = #{status}
WHERE id = ${id}
